import osimport jsondef save_as_json(data, folder, file_name):    with open(os.path.join(folder, file_name), 'w', encoding='utf-8') as f:        json.dump(data, f, ensure_ascii=False)def read_json_as_dict(folder, file_name):    with open(os.path.join(folder, file_name)) as f:        data = json.load(f)        dict_data = json.loads(data)    return dict_data